import React, { useState, useRef } from 'react';
import './AdminPage.css';
import Logo from '../components/Logo';

// Helper: Convert File to base64
function toBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onloadend = () => resolve(reader.result);
    reader.onerror = reject;
    reader.readAsDataURL(file);
  });
}

function AdminPage({ storeItems, setStoreItems, onBackToStore, activityLog = [], setActivityLog }) {
  const [newItem, setNewItem] = useState({
    name: '',
    price: '',
    imageFile: null,
    image: '' // base64 image
  });

  const fileInputRef = useRef(null);

  const [filterPrefix, setFilterPrefix] = useState('');

  const handleAddItem = async () => {
    const { name, price, imageFile } = newItem;

    if (!name || !price || !imageFile) {
      alert("Please fill in all fields and add an image.");
      return;
    }

    const base64Image = await toBase64(imageFile);

    const newProduct = {
      id: Date.now(),
      name,
      price: parseFloat(price),
      image: base64Image,
      description: 'Added via admin panel' // üëà new!
    };

    try {
      const res = await fetch('http://localhost:3001/api/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          ...newProduct,
          imageUrl: base64Image // üëà required by PayScreen
        })
      });

      if (!res.ok) throw new Error('Failed to save product to server');

      // If success, add to store
      setStoreItems(prev => [...prev, newProduct]);

      // Optionally show success message
      console.log('‚úÖ Product saved to server');
    } catch (err) {
      console.error('‚ùå Error saving product to server:', err);
      alert('Failed to save product to server.');
    }

    setNewItem({ name: '', price: '', imageFile: null, image: '' });
    fileInputRef.current.value = null;

    if (setActivityLog) {
      setActivityLog(prev => [
        ...prev,
        {
          datetime: new Date().toLocaleString(),
          username: 'admin',
          activity: `Added product: ${name}`
        }
      ]);
    }
  };

  const handleRemove = (id) => {
    const removedItem = storeItems.find(item => item.id === id);
    setStoreItems(prev => prev.filter(item => item.id !== id));

    if (setActivityLog && removedItem) {
      setActivityLog(prev => [
        ...prev,
        {
          datetime: new Date().toLocaleString(),
          username: 'admin',
          activity: `Deleted product: ${removedItem.name}`
        }
      ]);
    }
  };

  const handleImageUpload = (file) => {
    if (!file || !file.type.startsWith("image/")) {
      alert("Please upload a valid image file.");
      return;
    }

    toBase64(file).then(base64 => {
      setNewItem(prev => ({
        ...prev,
        imageFile: file,
        image: base64
      }));
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) handleImageUpload(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) handleImageUpload(file);
  };

  const handleDragOver = (e) => e.preventDefault();

  const filteredActivity = (activityLog || []).filter(log =>
    log.username?.toLowerCase().startsWith(filterPrefix.toLowerCase())
  );

  return (
    <div className="admin-container">
      <Logo></Logo>
      <h2>Manage Products</h2>
      <input
        type="text"
        placeholder="Name"
        value={newItem.name}
        onChange={e => setNewItem(prev => ({ ...prev, name: e.target.value }))}
      />
      <input
        type="number"
        placeholder="Price"
        value={newItem.price}
        onChange={e => setNewItem(prev => ({ ...prev, price: e.target.value }))}
      />

      <div
        className="image-drop-area"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        <p>Drag & drop an image or choose a file</p>
        <input
          ref={fileInputRef}
          type="file"
          accept="image/*"
          onChange={handleFileChange}
        />
        {newItem.imageFile && typeof newItem.image === 'string' && newItem.image.startsWith('data:image') && (
          <div className="image-preview">
            <img src={newItem.image} alt="Preview" />
            <button
              className="remove-image"
              onClick={() =>
                setNewItem(prev => ({ ...prev, imageFile: null, image: '' }))
              }
            >‚ùå</button>
          </div>
        )}
      </div>

      <button className="add-button" onClick={handleAddItem}>Add Product</button>

      <h3>Current Products:</h3>
      <div className="products-list">
        {storeItems.map(item => (
          <div key={item.id} className="product-row">
            <div className="product-details">
              <strong>{item.name}</strong> - ${item.price.toLocaleString()}
            </div>
            <button className="delete-button" onClick={() => handleRemove(item.id)}>Delete</button>
          </div>
        ))}
      </div>

      <h3>Activity Log</h3>
      <input
        type="text"
        placeholder="Filter by username prefix"
        value={filterPrefix}
        onChange={e => setFilterPrefix(e.target.value)}
        className="filter-input"
      />

      <table className="activity-table">
        <thead>
          <tr>
            <th>DateTime</th>
            <th>Username</th>
            <th>Activity</th>
          </tr>
        </thead>
        <tbody>
          {filteredActivity.map((log, idx) => (
            <tr key={idx}>
              <td>{log.datetime}</td>
              <td>{log.username}</td>
              <td>{log.activity}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <button className="back-btn" onClick={onBackToStore}>‚Üê Back to Store</button>
    </div>
  );
}

export default AdminPage;
