import React, { useState } from 'react';
import Logo from '../components/Logo';
import './ContactPage.css';

export default function ContactPage({ onBackToStore, user }) {
  const [form, setForm] = useState({
    name: user?.username || '',
    email: '',
    subject: '',
    message: '',
  });
  const [status, setStatus] = useState(null);

  const onChange = (e) => setForm(f => ({ ...f, [e.target.name]: e.target.value }));

  const onSubmit = (e) => {
    e.preventDefault();
    if (!form.email || !form.message) {
      setStatus({ type: 'error', msg: 'Email and message are required.' });
      return;
    }
    // Simple mailto fallback so it works without a backend:
    const body = encodeURIComponent(`From: ${form.name}\nEmail: ${form.email}\n\n${form.message}`);
    const mailto = `mailto:info@example.com?subject=${encodeURIComponent(form.subject || 'Contact from site')}&body=${body}`;
    window.location.href = mailto;
    setStatus({ type: 'ok', msg: 'Opening your email client…' });

    // If you prefer a backend later:
    // fetch('http://localhost:3001/api/contact', { method: 'POST', headers: {'Content-Type':'application/json'}, body: JSON.stringify(form) })
  };

  return (
    <div className="contact-page">
      <Logo />
      <h1>Contact Us</h1>
      <p className="lead">Have a question? Send us a message and we’ll get back to you.</p>

      <form className="contact-form" onSubmit={onSubmit}>
        <label>
          <span>Name</span>
          <input name="name" value={form.name} onChange={onChange} placeholder="Your name" />
        </label>

        <label>
          <span>Email</span>
          <input name="email" type="email" value={form.email} onChange={onChange} placeholder="you@company.com" required />
        </label>

        <label>
          <span>Subject</span>
          <input name="subject" value={form.subject} onChange={onChange} placeholder="How can we help?" />
        </label>

        <label className="full">
          <span>Message</span>
          <textarea name="message" rows="5" value={form.message} onChange={onChange} placeholder="Tell us a little more…" required />
        </label>

        {status && <div className={`contact-status ${status.type}`}>{status.msg}</div>}

        <div className="form-actions">
          <button className="btn-primary" type="submit">Send</button>
          <button type="button" className="back-btn" onClick={onBackToStore}>← Back to Store</button>
        </div>
      </form>
    </div>
  );
}
