// src/pages/ContactPage.jsx
import React, { useEffect, useMemo, useState } from 'react';
import Logo from '../components/Logo';
import './ContactPage.css';

function ContactPage({ user, onBackToStore }) {
  const isAdmin = (user?.username || '').toLowerCase() === 'admin';

  // ===== USER FORM =====
  const [form, setForm] = useState({
    name: '',
    email: '',
    subject: '',
    body: '',
  });
  const [sending, setSending] = useState(false);
  const [result, setResult] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResult('');
    setSending(true);
    try {
      const res = await fetch('http://localhost:3001/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(form),
      });
      if (!res.ok) {
        const err = await res.json().catch(() => ({}));
        throw new Error(err.error || 'Failed to send');
      }
      setResult('Thanks, we’ll get back to you soon.');
      setForm({ name: '', email: '', subject: '', body: '' });
    } catch (err) {
      setResult(err.message || 'Send failed');
    } finally {
      setSending(false);
    }
  };

  // ===== ADMIN VIEW =====
  const [tab, setTab] = useState('pending'); // 'pending' | 'handled'
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadMessages = async () => {
    if (!isAdmin) return;
    setLoading(true);
    try {
      const res = await fetch(
        `http://localhost:3001/api/messages?status=${tab}`,
        { headers: { 'X-Username': user.username } }
      );
      if (!res.ok) throw new Error('Load failed');
      const data = await res.json();
      setMessages(Array.isArray(data) ? data : []);
    } catch (e) {
      setMessages([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadMessages(); /* eslint-disable-next-line */ }, [tab]);

  const markHandled = async (id) => {
    if (!isAdmin) return;
    try {
      const res = await fetch(
        `http://localhost:3001/api/messages/${id}/handle`,
        { method: 'PATCH', headers: { 'X-Username': user.username } }
      );
      if (!res.ok) throw new Error('Update failed');
      // Remove from current list immediately
      setMessages(prev => prev.filter(m => m.id !== id));
    } catch (e) {
      alert(e.message);
    }
  };

  const restoreMessage = async (id) => {
    if (!isAdmin) return;
    try {
      const res = await fetch(
        `http://localhost:3001/api/messages/${id}/restore`,
        { method: 'PATCH', headers: { 'X-Username': user.username } }
      );
      if (!res.ok) throw new Error('Restore failed');
      setMessages(prev => prev.filter(m => m.id !== id));
    } catch (e) {
      alert(e.message);
    }
  };

  return (
    <div className="contact-page">
      <Logo />
      <header className="contact-hero">
        <h1>Contact Us</h1>
        <p>Private aviation requires precision and discretion. Tell us how we can help.</p>
      </header>

      {!isAdmin ? (
        <form className="contact-form card" onSubmit={handleSubmit}>
          <div className="grid-2 gap">
            <label className="field">
              <span>Full Name *</span>
              <input
                value={form.name}
                onChange={e => setForm(f => ({ ...f, name: e.target.value }))}
                required
              />
            </label>
            <label className="field">
              <span>Company</span>
              <input
                value={form.company || ''}
                onChange={e => setForm(f => ({ ...f, company: e.target.value }))}
              />
            </label>

            <label className="field">
              <span>Email *</span>
              <input type="email"
                value={form.email}
                onChange={e => setForm(f => ({ ...f, email: e.target.value }))}
                required
              />
            </label>
            <label className="field">
              <span>Phone</span>
              <input
                value={form.phone || ''}
                onChange={e => setForm(f => ({ ...f, phone: e.target.value }))}
              />
            </label>
          </div>

          <label className="field">
            <span>Subject</span>
            <input
              value={form.subject}
              onChange={e => setForm(f => ({ ...f, subject: e.target.value }))}
              placeholder="Acquisition advisory / Off-market search / Charter / Other"
            />
          </label>

          <label className="field">
            <span>Message *</span>
            <textarea
              rows={6}
              value={form.body}
              onChange={e => setForm(f => ({ ...f, body: e.target.value }))}
              placeholder="Share your goals, timelines, and any airframe preferences…"
              required
            />
          </label>

          {result && <div className={`form-note ${/Thanks/i.test(result) ? 'ok' : 'err'}`}>{result}</div>}

          <div className="actions">
            <button className="btn-primary" type="submit" disabled={sending}>
              {sending ? 'Sending…' : 'Send Message'}
            </button>
            <button className="back-btn" type="button" onClick={onBackToStore}>← Back to Store</button>
          </div>
        </form>
      ) : (
        <section className="admin-messages card">
          <div className="admin-toolbar">
            <div className="tabs">
              <button
                className={tab === 'pending' ? 'tab active' : 'tab'}
                onClick={() => setTab('pending')}
              >Pending</button>
              <button
                className={tab === 'handled' ? 'tab active' : 'tab'}
                onClick={() => setTab('handled')}
              >Handled</button>
            </div>
            <button className="refresh" onClick={loadMessages}>↻ Refresh</button>
          </div>

          {loading ? (
            <div className="loading">Loading…</div>
          ) : messages.length === 0 ? (
            <div className="empty">
              {tab === 'pending' ? 'No pending messages.' : 'No handled messages yet.'}
            </div>
          ) : (
            <ul className="message-list">
              {messages.map(m => (
                <li key={m.id} className="message-item">
                  <div className="meta">
                    <strong>{m.name}</strong> · <a href={`mailto:${m.email}`}>{m.email}</a>
                    <span className="date">{new Date(m.createdAt).toLocaleString()}</span>
                  </div>
                  {m.subject && <div className="subject">{m.subject}</div>}
                  <div className="body">{m.body}</div>

                  <div className="row-actions">
                    {tab === 'pending' ? (
                      <button className="btn-ok" onClick={() => markHandled(m.id)}>✓ Mark handled</button>
                    ) : (
                      <button className="btn-warn" onClick={() => restoreMessage(m.id)}>↩︎ Restore</button>
                    )}
                  </div>
                </li>
              ))}
            </ul>
          )}

          <div className="actions mt">
            <button className="back-btn" type="button" onClick={onBackToStore}>← Back to Store</button>
          </div>
        </section>
      )}
    </div>
  );
}

export default ContactPage;
