// src/components/MenuButton.jsx
import React, { useState } from 'react';
import './MenuButton.css';

function MenuButton({ user, onNavigate, onLogout }) {
  const [open, setOpen] = useState(false);

  const handleClick = (target) => {
    onNavigate(target);
    setOpen(false);
  };

  return (
    <div className="menu-button-wrapper">
      <button className="hamburger" onClick={() => setOpen(!open)}>
        ‚ò∞ Menu
      </button>
      {open && (
        <div className="dropdown-menu">
          <button onClick={() => handleClick('store')}>üè™ Store</button>
          <button onClick={() => handleClick('cart')}>üõí Cart</button>
          <button onClick={() => handleClick('myItems')}>üßæ My Items</button>
          <button onClick={() => handleClick('about')}>‚ÑπÔ∏è About</button>


          {/* ‚úÖ Only show if admin */}
          {user?.username === 'admin' && (
            <button onClick={() => handleClick('admin')}>üßë‚Äçüíª Admin</button>
          )}

          <button className="logout" onClick={onLogout}>üö™ Logout</button>
        </div>
      )}
    </div>
  );
}

export default MenuButton;
