// src/pages/ContactPage.jsx
import React, { useState } from 'react';
import './ContactPage.css';
import Logo from '../components/Logo';

const initial = {
  fullName: '',
  company: '',
  email: '',
  phone: '',
  subject: '',
  message: '',
  preferredContact: 'Email',
  budget: 'Undisclosed'
};

function ContactPage({ user, onBackToStore }) {
  const [form, setForm] = useState(initial);
  const [submitting, setSubmitting] = useState(false);
  const [okMsg, setOkMsg] = useState('');
  const [errMsg, setErrMsg] = useState('');

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setOkMsg('');
    setErrMsg('');
    if (!form.fullName.trim() || !form.email.trim() || !form.message.trim()) {
      setErrMsg('Please fill name, email, and message.');
      return;
    }
    setSubmitting(true);
    try {
      const res = await fetch('http://localhost:3001/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Username': user?.username || ''
        },
        body: JSON.stringify(form)
      });
      if (!res.ok) {
        const j = await res.json().catch(() => ({}));
        throw new Error(j.error || 'Failed to send message.');
      }
      setOkMsg('Thank you — your message was received. Our team will contact you shortly.');
      setForm(initial);
    } catch (err) {
      setErrMsg(err.message);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="contact-container">
      <Logo />
      <h1 className="contact-title">Contact Us</h1>
      <p className="contact-subtitle">Private aviation requires precision and discretion. Tell us how we can help.</p>

      <div className="contact-grid">
        <form className="contact-form" onSubmit={onSubmit}>
          <div className="form-row">
            <label>
              <span>Full Name *</span>
              <input name="fullName" value={form.fullName} onChange={onChange} type="text" maxLength={80} required />
            </label>
            <label>
              <span>Company</span>
              <input name="company" value={form.company} onChange={onChange} type="text" maxLength={80} />
            </label>
          </div>

          <div className="form-row">
            <label>
              <span>Email *</span>
              <input name="email" value={form.email} onChange={onChange} type="email" maxLength={120} required />
            </label>
            <label>
              <span>Phone</span>
              <input name="phone" value={form.phone} onChange={onChange} type="tel" maxLength={40} />
            </label>
          </div>

          <div className="form-row">
            <label>
              <span>Preferred Contact</span>
              <select name="preferredContact" value={form.preferredContact} onChange={onChange}>
                <option>Email</option>
                <option>Phone</option>
                <option>WhatsApp</option>
              </select>
            </label>
            <label>
              <span>Budget</span>
              <select name="budget" value={form.budget} onChange={onChange}>
                <option>Undisclosed</option>
                <option>$1M–$3M</option>
                <option>$3M–$8M</option>
                <option>$8M–$20M</option>
                <option>other</option>
              </select>
            </label>
          </div>

          <label className="full">
            <span>Subject</span>
            <input name="subject" value={form.subject} onChange={onChange} type="text" maxLength={120} placeholder="Acquisition advisory / Off-market search / Charter / Other" />
          </label>

          <label className="full">
            <span>Message *</span>
            <textarea name="message" value={form.message} onChange={onChange} rows={5} maxLength={2000} required placeholder="Share your goals, timelines, and any airframe preferences…" />
          </label>

          {errMsg && <div className="form-error">{errMsg}</div>}
          {okMsg && <div className="form-ok">{okMsg}</div>}

          <div className="actions">
            <button className="btn-primary" type="submit" disabled={submitting}>
              {submitting ? 'Sending…' : 'Send Message'}
            </button>
            <button className="btn-outline" type="button" onClick={onBackToStore}>← Back to Store</button>
          </div>
        </form>

        <aside className="contact-card">
          <h3>Our Office</h3>
          <p>Discreet meetings by appointment.</p>
          <div className="info">
            <div><strong>Email:</strong> clientdesk@example.com</div>
            <div><strong>Phone:</strong> +1 (555) 555-0199</div>
            <div><strong>Hours:</strong> Mon–Fri, 09:00–18:00</div>
          </div>
          <p className="note">We typically respond within one business day.</p>
        </aside>
      </div>
    </div>
  );
}

export default ContactPage;
