import React, { useState } from 'react';
import Logo from '../components/Logo';
import './FAQPage.css';

const FAQS = [
  { q: 'How do I purchase a jet?', a: 'Add your jet to the cart, go to Checkout, and confirm payment.' },
  { q: 'Do you offer maintenance planning?', a: 'Yes — covered in our After-sale services.' },
  { q: 'How can I leave a review?', a: 'Log in and open the Reviews page. Fill the form and submit.' },
  { q: 'Who can delete reviews?', a: 'Only the admin user can delete reviews.' },
];

export default function FAQPage({ onBackToStore }) {
  const [open, setOpen] = useState(null);

  return (
    <div className="faq-page">
      <Logo />
      <h1>Frequently Asked Questions</h1>

      <ul className="faq-list">
        {FAQS.map((item, i) => (
          <li key={i} className={`faq-item ${open === i ? 'open' : ''}`}>
            <button
              type="button"
              className="faq-q"
              onClick={() => setOpen(open === i ? null : i)}
              aria-expanded={open === i}
            >
              <span>{item.q}</span>
              <span className="faq-toggle">{open === i ? '–' : '+'}</span>
            </button>
            {open === i && <div className="faq-a">{item.a}</div>}
          </li>
        ))}
      </ul>

      <div className="footer-actions">
        <button className="back-btn" onClick={onBackToStore} type="button">← Back to Store</button>
      </div>
    </div>
  );
}
